/**                                // golfed version: 96 chars
#define mainImage( O, u )  \
    vec2 U = u/iResolution.xy, V = vec2( 1.2*U.x-.1 , 5.*U.y-2. ); \
	O += fract(V)==V ? .5 : U.x
/**/


void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    O = vec4(U.x);
    U = vec2( 1.2*U.x-.1 , (U.y-.4)*5. );
	if (fract(U)==U) O = vec4(.5);
}
