//On/Off Switches
bool showHouse = true;
bool showChimney = true;
bool showWindows = true;

//Colors
vec3 red = vec3(1.0, 0.0, 0.0);
vec3 green = vec3(0.0, 1.0, 0.0);
vec3 blue = vec3(0.0, 0.0, 1.0);

//Shapes
float rectHouse, rectChimney, rectWindows;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
vec2 uv = fragCoord.xy / iResolution.xy;

//Correct for aspect ratio
uv.y *= iResolution.y / iResolution.x;

//Layers
vec4 BG = vec4(1.0, 1.0, 1.0, 1.0);
vec4 LayerHouse;
vec4 LayerChimney;
vec4 LayerWindow1;
vec4 LayerWindow2;
vec4 LayerWindows;

//Draw House
float rectHouseX = step(.2, uv.x) * step(uv.x, .8);
float rectHouseY = step(.0, uv.y) * step(uv.y, .4);
rectHouse = rectHouseX * rectHouseY;
LayerHouse = vec4(red, rectHouse);

//Draw Chimney
float rectChimneyX = step(.2, uv.x) * step(uv.x, .25);
float rectChimneyY = step(.0, uv.y) * step(uv.y, .5);
rectChimney = rectChimneyX * rectChimneyY;
LayerChimney = vec4(green, rectChimney);

//Draw Windows
float rectWindow1X = step(.35, uv.x) * step(uv.x, .45);
float rectWindow1Y = step(.10, uv.y) * step(uv.y, .2);
rectWindows = rectWindow1X * rectWindow1Y;
LayerWindow1 = vec4(blue, rectWindows);

float rectWindow2X = step(.6, uv.x) * step(uv.x, .7);
float rectWindow2Y = step(.10, uv.y) * step(uv.y, .2);
rectWindows = rectWindow2X * rectWindow2Y;
LayerWindow2 = vec4(blue, rectWindows);

LayerWindows = mix(LayerWindow1, LayerWindow2, LayerWindow2.a);

//Determines whether of show Variables are true/false and acts accordingly
LayerHouse *= float(showHouse);
LayerChimney *= float(showChimney);
LayerWindows *= float(showWindows);

// Blend the three
vec4 Composite = BG;
Composite = mix(BG, LayerHouse, LayerHouse.a);
Composite = mix(Composite, LayerChimney, LayerChimney.a);
Composite = mix(Composite, LayerWindows, LayerWindows.a);


fragColor = Composite;
}
