void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 samplexy = fragCoord / iResolution.xy;

    samplexy.x += sin(samplexy.x * 10.0 + iTime * 2.0) * 0.05 + iTime * 0.1;

    vec4 img = texture(iChannel0, samplexy);

    float grad = fragCoord.x / iResolution.x;
    img.r *= grad;
    img.g *= 0.7;
    img.b *= 1.0 - grad;

	fragColor = img;
}
