void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    const float perturbation_gain = 0.01;

    vec2 perturbation_uv = 2.0 * texture(iChannel1, uv).xy - vec2(1.0, 1.0);
    float perturbation_amount = perturbation_gain * sin(iTime);
    vec2 perturbed_uv = uv - perturbation_amount * perturbation_uv;    
    vec3 london_rgb = texture(iChannel0, perturbed_uv).rgb;

	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = vec4(london_rgb, 1.0);
}
