void mainImage( out vec4 O, vec2 U )
{
    float v = .5+.5*sqrt(-U/U).x, // NaN
          y = U.y/iResolution.y;

	O = vec4(  y > .5 ?  min(.5,v)
             : y > .49 ? sin(U.x+U.y) // separator
             :           max(.5,v)
             );

 // O = vec4(O==vec4(.5));  // if you want to be sure
    O.r++; O.g--;
}
