void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = fragCoord.x - iResolution.x/2.0;
    float b = fragCoord.y - iResolution.y/2.0;
    float distance = sqrt(a*a + b*b);
    float angle = atan(a, b);

	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(-iTime*6.0+distance/6.0)+sin(iTime)+sin(angle*10.0+iTime+distance/15.0),1.0);
}
