/*
based on my Crumble Spiral https://www.shadertoy.com/view/4dd3Ds
*/

void mainImage( out vec4 f, vec2 v )
{
    f.xyz = iResolution;

    vec2 g = v/40.;

	for (int i = 0; i < 60; i++) 
        f.z += length((v-f.xy*.5)*f.z/f.y + sin(f.z - 4.*iDate.w + vec2(1.6,0))) - 2.

        + (vec4(.34,1,1,1)/1.
              / cos(3.14*(  g.y + g.x * sign( cos( 1e3 * length(floor(g)) ))))).x
		;
    f = vec4(1,2,3,1) / (2. + .2*f*f).z;
}
