void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float wave = 0.5*sin(uv.x*4.0+iTime*2.0)+0.5;
    vec4 bgColor1 = vec4(uv,0.1+0.5*sin(iTime),1.0);
    vec4 bgColor2 = vec4(0.1+0.5*sin(iTime),uv,1.0);
    float smooths = smoothstep(wave+0.01,wave-0.01,uv.y);
    fragColor = mix(bgColor1, bgColor2, smooths);
}
