#define PI 3.1415926535897932384626433832795





// Speed of rotation

#define S (PI/16.0)


// ZOOM

#define Z 1.0




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cc = uv - vec2(0.5,0.5);

    vec2 po = vec2( atan(cc.x,cc.y) , length(cc) );

    float fft=texture(iChannel1,vec2(0.25,0.25)).r; 
    fft+=texture(iChannel1,vec2(0.125,0.25)).r; 

float N=ceil(16.0*fft);
float F=(2.0*PI/N);

    float m=mod((2.0*PI)+po.x,F);

    if(m>F/2.0){ m=F-m; }


    po.x = m+(iTime*S);
    po.y /=Z;

    cc= vec2(cos(po.x),sin(po.x))*po.y;


    // Output to screen
    fragColor = texture( iChannel0, cc+vec2(0.5,0.5) ).rgba;

    fragColor = fragColor*4.0-2.0;

}
