void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col1 = texture(iChannel0, vec2(uv.x, iTime/128.0)).rgb;
    vec3 col2 = texture(iChannel1, vec2(iTime/127.0, uv.y)).rgb;
    //vec3 col = col1 * col2;
    vec3 col = 1.0 -(1.0 - col1) * (1.0 - col2);

    // Output to screen
    fragColor = vec4(col,1.0);
}
