float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 pattern(vec2 p){ // (distance,region)
    p *= 3.;
    p += 0.5;
    float d = 1000000.;
    float t = iTime;
    float e = 0.1;// random seed
    for(int i=0;i<20;i++){
        float w = pow(2.,float(i));
        float f,r;
		vec2 v;

        f = rand(vec2(0,float(i))+e);
        f *= f;
        r = rand(vec2(1,float(i))+e) * 3.141592 * 2.;
        v = vec2(cos(r),sin(r));
        if(dot(p,v) < f)e+=w;
        else e-=w;
       	float u = abs(dot(p,v)-f);
        d = min(u,d);
    }
    return vec2(d,e);
}

vec2 normal(vec2 p){
    vec2 e = vec2(0.0001,0);
    return normalize(vec2(
        pattern(p+e.xy).x - pattern(p-e.xy).x,
        pattern(p+e.yx).x - pattern(p-e.yx).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy; 
    vec2 def = fragCoord / R;
	vec2 uv = (2.*fragCoord.xy - R)/R.y;

    vec2 res = pattern(uv);
    float dist = res.x<0.2?1.0-5.0*res.x:0.0;
    vec2 n2 = normal(uv);
    vec3 n = normalize(vec3(n2*dist,2.0));
    vec2 diff = vec2(rand(vec2(0.,res.y)),rand(vec2(1.,res.y))) * 2.0 - 1.0;
    diff /= 6.0;
    vec3 point = vec3(vec2(uv.x,-uv.y) + diff,0.9);
    float r1 = -0.3;
    float r2 = 0.9 + iTime/10.0;
    point *= mat3(1,0,0,0,cos(r1),sin(r1),0,-sin(r1),cos(r1));
    point *= mat3(cos(r2),0,sin(r2),0,1,0,-sin(r2),0,cos(r2));
    vec3 u = texture(iChannel0,point).xyz;
    u *= pow(1.-dist,0.1);
    fragColor = vec4(u,1);
}
