void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 point = fragCoord.xy/iResolution.xy;

    vec4 color1 = texture(iChannel0, point);
    vec4 color2 = texture(iChannel1, point);
    float rDiff = abs((13.0/255.0)-color1.r);
    float gDiff = abs((161.0/255.0)-color1.g);
    float bDiff = abs((37.0/255.0)-color1.b);
    if(rDiff < 0.275 && gDiff < 0.275 && bDiff < 0.275) {
        color1 = color2;
    }
    fragColor = color1;// * color2;
}
