float map(float value, float oldMin, float oldMax, float newMin, float newMax) {
    return newMin + (newMax - newMin) * (value - oldMin) / (oldMax - oldMin);
}

float square(vec2 point, vec2 dimensions) {
  vec2 origin = vec2(sin(iTime * 2.0)/3.0, 0.0);
  vec2 d = abs(point - origin) - dimensions;
  float dist = min(max(d.x, d.y),0.0) + length(max(d,0.0));
  float m = map(point.x, -1.0, 1.0, 0.0, 1.0);
  return dist < 0.0 ? m : 1.0 - m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Fixes the aspect ratio to match a square frame
	// get values between -1 and +1 for the current pixel
    vec2 res = iResolution.xy;
	vec2 uv = (2.0 * fragCoord - res) / res.y;
    float blue = square(uv, vec2(.5, .5));
	vec3 color = vec3(.3 * blue, .3 * blue, blue);
    fragColor = vec4(color,1.0);
}
