float FOV = 60.; //60 degrees FOV
const float pi = acos(-1.);
vec2 project( vec3 p ) {
    float RAD = FOV/360.*pi;
    float DVN = p.z;
    return p.xy/DVN+.5;
}
float line ( vec2 p, vec2 a, vec2 b, float r ) {

    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    vec2 ac = abs( pa - ba*h );
    return ac.x+ac.y - r;

}
float lp(vec2 p, vec2 a, vec2 b) {
	float xP = (p.x-min(a.x,b.x));
    float xFP = xP/abs(a.x-b.x);
    float yP = (p.y-min(a.y,b.y));
    float yFP = yP/abs(a.y-b.y);
    return xFP>yFP?xP:yP;
}
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
mat3 rmx(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s, 
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,  
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c
              );
}
vec3 poly[4];
vec2 proj[4];
void polyShape() {
	poly[0]=vec3(-1, -1, 0);
    poly[1]=vec3(1, -1, 0);
    poly[2]=vec3(1, 1, 0);
    poly[3]=vec3(-1,1,0);    
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    polyShape();
	vec2 uv = fragCoord.xy / iResolution.xy;
    float df = 5000000.;
    for (int i = 0; i < 4; i++) {
    	proj[i]=project(poly[i]*rmx(vec3(cos(iTime),1,sin(iTime)), iTime)+vec3(0,0,3));    
    }
    float lv = 0.;
    for (int i = 0; i < 4; i++) {
        vec2 nv =  proj[i+1];
        if (i+1>3){
        	nv=proj[0];    
        }
    	df=min(df,line(uv, proj[i],nv, .002)); 
		lv=lp(uv,proj[i],nv);
    }

    vec3 c = vec3(0);
    if (df<.02) {
     	vec3 ct = hsv2rgb(vec3(lv,1,1));
        c=pow(1.-df/.02,5.)*ct;
    }
	fragColor = vec4(c,1.0);
}
