#define PI acos(-1.)

float innerRadius = 0.1;
float outerRadius = 0.49;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (fragCoord.xy*2. - iResolution.xy)/iResolution.y * .5 ;  //Thanks aiekick

    float theta = atan( uv.y, uv.x );
    float dist = length( uv );

    //Make center circle and edge black
   	float a = smoothstep( innerRadius - 0.001, innerRadius + 0.001, dist );
    a *= 1.0 - smoothstep( outerRadius - 0.001, outerRadius + 0.001, dist );

    uv = vec2((theta + PI) / (PI * 2.0),
    		  (dist - innerRadius) / (outerRadius - innerRadius)
             );

	fragColor = texture(iChannel0 , uv) * a;
}
