void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;

    uv.y+=sin(uv.x*7.0-iTime)*0.03;
    uv.x+=cos(uv.x*3.3+iTime)*0.1;
    uv.y*=sin(iTime*0.1+0.5)*2.3;
    float s = .2,
        t = iTime*.3,
        y = mod(exp(uv.y+0.7)+t,0.4),
        x = uv.x+t*.2;
        y += sin(t*1.0)*0.5;
    float k = smoothstep(.0,uv.y+y, sin(x * 7.)*s+.45);
    float d = smoothstep(0.42,.44,length(uv-0.5));
    vec3 hey = vec3(1.,.1,.2),        
        hoo = vec3(.0,.0,.2),
    thisisit = mix(hey,hoo,k);   
    O = vec4(thisisit,1.0);
}
