float sdCircle(in vec2 p, in vec2 pos, float radius)
{
    return length(p-pos)-radius;
}

float sdBox(in vec2 p, in vec2 pos, in vec2 size)
{
    vec2 d = abs(p-pos)-size;
    return min(0.0, max(d.x, d.y))+length(max(d,0.0));
}

// https://www.shadertoy.com/view/ll2GD3
vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    return a + b*cos(6.28318*(c*t+d));
}

// polynomial smooth min (k = 0.1);
float sminCubic(float a, float b, float k)
{
    float h = max(k-abs(a-b), 0.0);
    return min(a, b) - h*h*h/(6.0*k*k);
}

float opU(float d1, float d2)
{
    return min(d1, d2);
}

float opBlend(float d1, float d2)
{
    float k = 0.2;
    return sminCubic(d1, d2, k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float aspect = iResolution.x/iResolution.y;

    float d = sdCircle(p, vec2(sin(iTime*0.2)*0.5+1.5,cos(iTime*0.3)*0.2), 1.3);
    d = opU(d, sdCircle(p, vec2(cos(iTime*0.2)*0.5,sin(iTime*0.3)*0.2), 0.3));
    d = opU(d, sdBox(p, vec2(sin(0.5+iTime*0.3)*0.6,sin(0.6+iTime*0.5)*0.7),
                     vec2(0.4,0.25)));

    d = opBlend(d, max(-sdCircle(p, iMouse.xy/iResolution.xy *
                        vec2(aspect*2.0,2.0) -
                        vec2(aspect,1.0), 0.15),
                   sdCircle(p, iMouse.xy/iResolution.xy *
                        vec2(aspect*2.0,2.0) -
                        vec2(aspect,1.0), 0.5)));

    //d = max(d, sdCircle(p, vec2(0.0), 0.9));

    float maxDist = 2.0;
    vec3 palCol = pal(clamp(0.5-d*0.4, -maxDist,maxDist), 
                      vec3(0.3,0.3,0.0),vec3(0.8,0.8,0.1),vec3(0.9,0.7,0.0),vec3(0.3,0.9,0.8));

    vec3 col = palCol;//vec3(1.0) - d*vec3(-0.5,0.9,0.2);
    // Darken around surface
	col = mix(col, col*1.0-exp(-10.0*abs(d)), 0.4);
	// repeating lines
    col *= 0.8 + 0.2*cos(150.0*d);
    // White outline
    col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)));

    // dithering:
    //col += (1.0/255.0)*texture( iChannel0, fragCoord.xy/iChannelResolution[0].xy ).xyz;

    fragColor = vec4(col, 1.0);
}
