void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 texColor = texture(iChannel0,xy);
    float v = max(max(texColor.x, texColor.y), texColor.z);

    float pi = 3.14f;
    float condition1 = (sin(iTime) + 1.0f) / 2.0f;
    float condition2 = (sin(2.0f * iTime) + 1.0f) / 2.0f;
    float condition3 = (sin(3.0f * iTime) + 1.0f) / 2.0f;
    float r = 0.0f;
    float g = 0.0f;
    float b = 0.0f;
    if (v <= condition1)
    {
        r = 0.0f;
    }
    else
    {
        r = v;
    }  
    if (v <= condition2)
    {
        g = 0.0f;
    }
    else
    {
        g = v;
    }  
    if (v <= condition3)
    {
        b = 0.0f;
    }
    else
    {
        b = v;
    }    

    fragColor = vec4(r, g, b, texColor.z);
}
