void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float bias1 = -1.0f;
    float bias2 = 0.0f;

    float value1 = 10.0f;
    float value2 = -10.0f;

    vec4 color = value1 * texture(iChannel0, uv, bias1) + value2 * texture(iChannel0, uv, bias2);


    fragColor = color.rgba;

}
