void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	    
	float ca = 0.04;
	float cb = 0.101;
	float gt = iTime;
    float gtc = 2.0 + (30.0 * sin(gt * 0.125));

	float algo = 0.0;

	algo += (1.32 * ca) * ceil(((1.0-uv.x) * 0.50 * gtc)*((1.0-uv.y) * 0.271 * gtc) * 21.00 * cb);
    algo -= (1.32 * ca) * ceil(((uv.x) * 0.50 * gtc)*((uv.y) * 0.271 * gtc) * 21.00 * cb);

	fragColor = vec4(algo*0.1, algo*0.1, algo*0.9, 1.0);
}
