#define tex(U) texture(iChannel0, U/4./iResolution.xy).rrrr
//#define T(U)  2. *  tex(U)
#define T(U) (1. * (tex(U)-.55) + 1.)
#define S(U) (tex(U)-.5)

void mainImage( out vec4 O,  vec2 U )
{
    // --- Perlin casdade additive
  //O = ( T(U) + T(2.*U)/2. + T(4.*U)/4. + T(8.*U)/8. + T(16.*U)/16. + T(32.*U)/32.  ) / 2. / 2. ;
  //O = ( S(U) + S(2.*U)/2. + S(4.*U)/4. + S(8.*U)/8. + S(16.*U)/16. + S(32.*U)/32.  ) / 2. +.5;

    // --- Perlin flat additive
  //O = (( T(U) + T(2.*U) + T(4.*U) + T(8.*U) + T(16.*U) + T(32.*U) ) / 6. / 2. -.5) *2. +.5;
  //O = ( S(U) + S(2.*U) + S(4.*U) + S(8.*U) + S(16.*U) + S(32.*U) ) / 6.  * 2. + .5;

    // --- Perlin multiplicative
    O =   T(U) * T(2.*U) * T(4.*U) * T(8.*U) * T(16.*U) * T(32.*U) * T(64.*U)  * T(128.*U) / 2. ;

    // --- various kind of contrast change
  //if (mod(iTime,2.)>1.) O++;
    if (mod(iTime,2.)>1.5) O++;
  //if (mod(iTime,2.)>1.) O -= O;
  //if (mod(iTime,2.)>1.) O = 1.-O;

#if 0  // check overshoots   
    if (O.x>1.) O = vec4(1,0,0,0);
    if (O.x<0.) O = vec4(0,0,1,0);
#endif    

  //O = sqrt(O); // gamma
}
