void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    fragColor = texture(iChannel1, uv);


    float r = 13. / 255.;
    float g = 163. / 255.;
    float b = 38. / 255.;

    vec3 bkg = vec3(r, g, b);
    vec3 vid = fragColor.rgb;

    float d = length(vid-bkg);
    if(d < 0.3333)
        fragColor.r = 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    

    float r = 13. / 255.;
    float g = 163. / 255.;
    float b = 38. / 255.;

    vec3 bkg = vec3(r, g, b);
    vec3 vid = texture(iChannel0, uv).rgb;

    uv.y -= iTime;

    float d = length(vid-bkg);
    if(d < 0.3333)
        fragColor = vec4(0,0,0,1);
    else
        fragColor = vec4(1,0.5,0,1)
        * texture(iChannel1, uv * 0.3333) + vec4(vid*vid*.25,1);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    fragColor = texture(iChannel0, uv);
    uv.y -= .003;
    fragColor *= texture(iChannel2, uv);
    fragColor += texture(iChannel1, uv);
    fragColor *= 0.9;

}
