void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y -= 0.5;

    float x = uv.x;
    float x1 = -x;
    float x2 = (x*12.-3.2);

    float y1 = sqrt(x*.1);
    float y2 = exp(x1*48.+3.5)+.04;
	float y3 = min(y1,y2);
    float y4 = x2*x2*x2+.04;
    float y5 = max(min(.115,y4),0.);
    float y6 = -((x-.3)*(x-.3))*12.+.115;
    float y7 = y6+y5-.115;
    float y8 = max(y3,y7);
    float y9 = -x*.75+.7;
    float y10 = max(min(y8,y9),0.);
    float y11 = min(0.,-y10);

    float d = distance(uv,vec2(.075,.0));
    d = 1.-smoothstep(.02,.025,d);

    float c = (smoothstep(y10,y10+.01, uv.y))+(1.-smoothstep(y11,y11+.01, uv.y))+d;

    vec3 color = vec3(c);

    // Output to screen
    fragColor = vec4(color,1.0);
}
