void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x = uv.x * 2.0 - 1.0;
    uv *= length(uv) + 2.0;
    uv.y -= 1.25;
    uv.x *= iResolution.x / iResolution.y;
	fragColor = vec4(cos(uv.y)) + vec4(-0.025, 0.025, 0.6, 1.0);
    fragColor.g += cos(sin(uv.x/uv.y) - uv.x) * 0.5 * fract(length(uv)) / length(uv);
}
