float timeMod(){
    return mod(iTime, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float envelope = exp(-timeMod()*4.5);
    float offsetX = envelope*0.08*sin(timeMod()*8.0);
    float offsetY = envelope*0.08*cos(timeMod()*8.0);
    vec3 ret = texture(iChannel0, uv).rgb;
    ret = mix(ret, texture(iChannel0, vec2(uv.x + offsetX, uv.y + offsetY)).rgb, 0.5);
    ret = mix(ret, texture(iChannel0, vec2(uv.x - offsetX, uv.y - offsetY)).rgb, 0.5);
	fragColor = vec4(ret,1.0);
}
