void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float cols= 5.0;
    float rows = 5.0;
    vec3 col1 = vec3(0.0,0.0,0.0);
    vec3 col2 = vec3(1.0,1.0,1.0);

	if ((mod(floor(uv.y * rows), 2.0)) == 0.0)
        if ((mod(floor(uv.x * cols), 2.0)) ==0.0)
            fragColor = vec4(col1, 1.0);
        else 
            fragColor = vec4(col2, 1.0);
    else 
        if ((mod(floor(uv.x * cols), 2.0))  == 0.0)
            fragColor = vec4(col2, 1.0);
        else
            fragColor = vec4(col1,1.0);

}
