const vec2 cols = vec2(8., 8.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mUv = iMouse.xy / iResolution.xy;
    vec2 square = floor(uv*cols)/cols;
    vec2 mSquare = floor(mUv*cols)/cols;
    if(square == mSquare) {
        fragColor = vec4(square*1.5, 0.,1.0);
    } else {
        fragColor = vec4(texture(iChannel0,uv).xyz*0.93,1.0);
    }
}
