const float PI = 3.14159;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = vec2((fragCoord.x - iResolution.x*.5) / iResolution.y, (fragCoord.y - iResolution.y*.5) / iResolution.y);

  uv *= 2.0;
  float factor = 0.;
  float f2 = 0.;
  float radius = 0.54;
  const int count = 20;
  for(int i = 0;i != count;++i)
  {
    float offset = sin(2. * PI * float(i) / float(count) + iTime * 0.4);
    float base = length(uv-vec2(offset, sin(iTime + 2. * PI * float(i) / float(count)))) + radius;

    factor += smoothstep(0., 1., 1.0 - base);
    factor += 0.2 * smoothstep(0., 1., 1.0 - base - sin(iTime) * 0.05);
    f2 += smoothstep(0., 1., 1.0 - base);
    factor += 0.1 * factor * texture(iChannel0, uv).x;
  }

  vec3 color = vec3(factor * 0.4, factor, factor)*0.6 + vec3(0.1,(sin(iTime*0.5-0.4)*0.5+0.5)*0.3,0.3);
  color += vec3(0, uv.y*0.4*factor, 0) + f2 * length(texture(iChannel1, uv).xyz);
  fragColor = vec4(color,1);
}
