vec3 GetColor(float normalizedValue)
{
    float angle = normalizedValue * 360.;
    if(angle < 60.)
    {
    return vec3(1.,(angle/60.),0.);   
    }
    else if(angle < 120.)
    {
        return vec3(1.-((angle - 60.)/60.),1.,0.);
    }
    else if(angle < 180.)
    {
        return vec3(0.,1.,((angle-120.)/60.));
    }
    else if(angle < 240.)
    {
        return vec3(0.,1. - (angle -180.)/60.,1.);
    }
        else if(angle < 300.)
    {
        return vec3((angle -240.)/60.,0.,1.);
    }
            else// if(angle <= 360.)
    {
        return vec3(1.,0.,1.-(angle -300.)/60.);
    }
}

float GetDistanceFromPoint(vec2 point, vec2 fragCoord)
{
    float xDist = abs(point.x- fragCoord.x);
    float yDist = abs(point.y - fragCoord.y);
    return sqrt(xDist*xDist + yDist*yDist);
}

float density = 4.;
float speed = 100.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float normalizedDistance = GetDistanceFromPoint(vec2(iResolution.x/2.,iResolution.y/2.),fragCoord);

    float soundValue = (texture(iChannel0, vec2(0.1, 0.0)).x);

    normalizedDistance -= speed*soundValue;


    normalizedDistance =mod(density* normalizedDistance,360.)/360.;
	fragColor = vec4(GetColor(normalizedDistance),1.0);
}
