#define ROWS 20.0
#define COLS 8.0
#define SCROLLSPEED 0.9
#define ALPHA 0.7
#define SCROLLBOARD true


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //resolve uv of pixel
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Scroll UV's
    if(SCROLLBOARD)
    	uv += iTime * SCROLLSPEED;

    //determine which square of the checkerboard you are on 
    float modcheck = mod(floor(ROWS * uv.x) + floor(COLS * uv.y),2.0);

    //sample from the iChannel0 (always occurs)
    vec4 sample1 = vec4(texture(iChannel0, uv - (iTime* SCROLLSPEED *2.0)).rgb,1.0);

    vec4 final = sample1; 

    //if the checkerboard is on a 'white' square, sample from iChannel1 and perform an alpha blend
    if(modcheck < 1.0)
    {
        vec4 sample2 = vec4(texture(iChannel1, uv + (iTime* SCROLLSPEED)).rgb, ALPHA);
    	final += sample2 * (1.0-sample2.a);  
    }

    //output final color
    fragColor = final;
}
