vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}
float circle(in vec2 uv, in float size){
    float dist = smoothstep(0.01,1.,exp(1.-distance(uv, vec2(.5))));
    return smoothstep(0.1,1.,(1.-distance(uv, vec2(.5))*size));
}

#define PI 3.14159265358979323846

float x(in vec2 uv, in float size){
    size *= 0.1;
    float crossRatio = .25;
    uv = rotate2D(uv,PI*0.25);
    float outup = 0.0;
    if(
        ((uv.x < .5+size) && 
        (uv.x > 0.5-size) && 
        (uv.y < 0.5+size*crossRatio) && 
        (uv.y > 0.5-size*crossRatio))
        ||
        ((uv.y < .5+size) && 
        (uv.y > 0.5-size) && 
        (uv.x < 0.5+size*crossRatio) && 
        (uv.x > 0.5-size*crossRatio))
    ) {
       outup = 1.;
    }

    return outup;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float grid = 5.;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 oUv = uv;
    uv *= grid;
    uv = fract(uv);

    float maxSize = 4.;
    float minSize = 40.;
    float size = (maxSize - minSize);
    size *= abs(sin(oUv.x*3.*oUv.y*iResolution.y/iResolution.x-iTime));
    size += minSize;

    //vec3 color = vec3(circle(uv,size));
    vec3 color = vec3(x(uv,2.));
    fragColor = vec4(color,1.0);
    //fragColor = vec4(vec3(abs(sin(oUv.x*oUv.y*iResolution.y/iResolution.x-iTime))),1.0);
}
