const float ringNumber = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec4 noise = texture(iChannel0, uv);

    vec3 lightWood = vec3(0.92, 0.75, 0.54);
    vec3 darkWood = vec3(0.29, 0.11, 0.05);

    float dist = length(uv);
    dist *= ringNumber;

    float r = fract(dist + (noise[0] + noise[1] + noise[2])/3.5) * 2.0;
    if(r > 1.0) {
      r = 2.0 - r;
    }

    fragColor = vec4(mix(lightWood, darkWood, r), 1.0);
}
