void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = iResolution.xy * 0.5;

    // animate center position
    float moveRad = 100.;
    float moveSpd = 5.;
    center.x += sin(iTime * moveSpd) * moveRad;
    center.y += cos(iTime * moveSpd) * moveRad;

   	float xDis = fragCoord.x-center.x;
    float yDis = fragCoord.y-center.y;
    float dis = sqrt(xDis*xDis + yDis*yDis);

    float rad = 200.;

    //fragColor = texture(iChannel0, uv);
    fragColor = texture(iChannel0, uv);

    if(dis < rad)
        fragColor.r += 0.5;
    else
        fragColor.b += 0.5;


}
