//vec4 colorBlack;
//vec4 colorWhite;
vec4 textureFire;
vec4 textureBubbles;

float tempX, tempY;

float numCols = 5.0;
float numRows = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    textureFire = texture(iChannel1, uv);
    textureBubbles = texture(iChannel0, uv);

    //colorBlack = vec4(0.0, 0.0, 0.0, 0.0);
    //colorWhite = vec4(1.0, 1.0, 1.0, 1.0);

    tempX = mod(floor(uv.x * numRows), 2.0);
    tempY = mod(floor(uv.y * numCols), 2.0);


    if((tempX == 0.0 && tempY == 0.0) || (tempX != 0.0 && tempY != 0.0))
    {
        //fragColor = colorBlack;
        fragColor = textureFire;
    }
    else
    {
        //fragColor = colorWhite;
        fragColor = textureBubbles;
    }    

}
