#define fmod(x,y) mod(floor(x),y)

#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 36.*16.*16.*1.5;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = 1.5;
    float p1 = 1.;
    vec3 col1 = col;

    for(int i=0;i<9;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 3; k++){

            //uv += fract(uv.x/2.)/2.; //gnarled lichen


            //uv += ceil(uv.y+uv.x)/19.; //another interesting pattern
            uv += (t2.yx);
            uv /= -1.35;


            //uv -= ceil(t2.x+p1)/2.; //mosaic pattern
            //uv -= ceil(t2.x*t2.y-p1)/2.; //another mosaic pattern
            //uv += vec2(1.); //this also makes an interesting pattern
            vec2 temp = t2;
            t2 =
                p1*triangle_wave(uv.yx-.5,1.5)
                //p1*triangle_wave(uv.yx-.5-fmod(t2.x/1.5,2.),1.5)
            ;
            t3 = -p1*triangle_wave(uv,1.5);
            uv.yx = (t2+t3)/scale;
            //uv += dot(uv,t2)/4.;
            p1 *= -1.;
            //uv += uv/scale;
        }
        float s1 = 2.-uv.y*2.;
        col.x = min(uv.y+uv.x+col.x,col.x*s1);
        //col.x = min(uv.y-uv.x+col.x,col.x*2.);

        //uv.x *= -1.;
        col = abs(col.yzx-vec3(col.x)*s1)/s1;
        col1 = abs(col1-col);
        //col = abs(col.yzx+vec3(col.x)*2.)/3.;

        uv.y *= -1.;
    }
    fragColor =
        vec4(col*2.,1.0);
        //vec4(col1*2.,1.0);
}
