void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 vec2 uv = (fragCoord - .5 * iResolution.xy) /iResolution.y;

    vec3 col =vec3(uv.x);
    vec3 trt = texture(iChannel0, uv * cos(iTime* .15)).rgb;
    vec3 trt2 = texture(iChannel0, (uv + 2.) * sin(iTime * .05)).rgb;
    float masklayer = vec3(trt2 + trt).r;
    vec3 colorOut = vec3(1.0, .0, .0);
    vec3 shit = trt - trt2;
    vec3 deg = shit * vec3(1.0, .5, .0);
    vec3 oc = vec3(deg.x); 
    vec3 outShit = vec3(1.0, 0.0, 0.0)*oc;


    vec3 trt34 = texture(iChannel0, uv).rgb;
    vec3 owc= vec3(.77) - vec3(trt34 *outShit) * vec3(.2 + abs(sin(iTime)));
    // Output to screen
    fragColor = vec4(1. - owc,1.0);
}
