struct Camera
{
    vec3 position;
    vec3 direction;
    float focalDistance;
};

Camera camera = Camera(
    vec3(0., 0., -.3), 
    vec3(1., 0., 1.),
    0.5);

struct Material 
{
    float diffuse;
    float specular;
    float shininess;
    float ambience;
    float reflection;
};

const Material material = Material(0.4, 0.2, 76.8, 0.8, 0.08);

struct Plane 
{
    vec3 position;
    vec3 normal;
    vec3 color;
    Material material;
};

Plane plane = Plane(
    vec3(0., -0.3, 0.), 
    vec3(0., 1., 0.), 
    vec3(0.2, 0.2, 0.2), 
    material);

struct PointLight
{
    vec3 position;
    float intensity;
};

PointLight light = PointLight(
    vec3(0., 1.5, 0.), // position
    10.);              // intensity

vec3 getLitColor(in vec3 viewDir, in vec3 surfacePointPosition, in vec3 objectColor, in PointLight pointLight, in vec3 surfaceNormal, in Material material)
{
    vec3 lightVector = surfacePointPosition - pointLight.position;
    vec3 lightDir = normalize(lightVector);   

   	float lightIntensity = (pow(0.1, 2.) / pow(length(lightVector), 2.)) * pointLight.intensity;

    float coeff = -dot(lightDir, surfaceNormal);     

    vec3 ambient = material.ambience * objectColor;

    vec3 diffuse = material.diffuse * max(coeff, 0.) * objectColor * lightIntensity;

    vec3 halfwayDir = normalize(lightDir + viewDir);  
    vec3 specular = pow(max(-dot(surfaceNormal, halfwayDir), 0.0), material.shininess) * material.specular * objectColor * lightIntensity;

    vec3 color = ambient + diffuse + specular;

    return color;
}

bool intersectPlane(in Plane plane, in vec3 origin, in vec3 rayDirection, out float t, out vec3 pHit) 
{    
    // Assuming vectors are all normalized
    float denom = dot(plane.normal, rayDirection); 
    if (denom < 1e-6) 
    { 
        vec3 p0l0 = plane.position - origin; 
        t = dot(p0l0, plane.normal) / denom; 

        if (t >= 0.)
        {
            pHit = origin + rayDirection * t;
            return true;
        }             
    } 

    return false; 
} 

#define RESOLUTION 0.01
#define SCALE .7
#define LAYER_COUNT 120

float getDepth(vec2 coord)
{
    return (1. - texture(iChannel1, coord).r) * SCALE;
}

vec3 getParallax(vec3 pHit, vec3 direction)
{        
    const float layerDepth = (1. * SCALE) / float(LAYER_COUNT);

    vec3 newPoint = pHit;

    for (int i = 0; i < LAYER_COUNT; i++)
    {
        float currentLayerDepth = layerDepth * float(i);
        vec3 samplePoint = pHit + direction * currentLayerDepth;

        float sampleDepth = getDepth(samplePoint.xz);

        if (currentLayerDepth > sampleDepth)
        {
            newPoint = samplePoint;
            newPoint.y = sampleDepth;
            return newPoint;
        }    
    }    

    return newPoint;
}

vec3 rayTrace(vec3 origin, vec3 direction)
{
    float dist;
    vec3 pHit;

    Plane parallaxPlane = plane; 

    if (intersectPlane(parallaxPlane, origin, direction, dist, pHit))
    {    
        pHit = getParallax(pHit, direction);

        vec3 down = pHit - vec3(0., 0., -RESOLUTION);
        down.y = getDepth(down.xz);
        down = normalize(down - pHit);

        vec3 right = pHit + vec3(RESOLUTION, 0., 0.);
        right.y = getDepth(right.xz);
        right = normalize(right - pHit);

        vec3 normal = normalize(cross(down, right));

        vec3 texColor = texture(iChannel1, pHit.xz).rrr;
        texColor *= vec3(0.4, 0.6, 0.64) * 1.3;
        vec3 litColor = getLitColor(direction, pHit, texColor, light, normal, plane.material);        

        vec3 reflColor = texture(iChannel0, reflect(direction, normal)).rgb;    

        vec3 finalColor = mix(litColor, reflColor, material.reflection);

        return finalColor;
    }

    return texture(iChannel0, direction).rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= (iResolution.x / iResolution.y); 

    float rotation = iTime / 3.;
    camera.position = vec3(cos(rotation), 0., sin(rotation)) * camera.focalDistance;
    camera.direction = normalize(vec3(0., clamp((iMouse.y / 200.) - 4., -4., -2.), 0.) - camera.position);
    vec3 right = normalize(cross(camera.direction, vec3(0., 1., 0.)));
    vec3 up = cross(right, camera.direction);
   	vec3 dir =  normalize(uv.x * right + uv.y * up + camera.direction);

    light.position = camera.position;

    vec3 finalColor = rayTrace(camera.position, dir); 

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
