#define matrot(x) mat2(cos(x),-sin(x),sin(x),cos(x))
#define amount 4.0
#define saturate(x) clamp(x,0.0,1.0)

highp float voronoi(
	highp vec2 x){
highp float n = 0.0;
for(int i = 0; i<int(amount); i++){
n = max(n, abs(
		sin(x.x)*
        cos(x.y))),

x *= matrot(0.25),

x += vec2(sin(iTime),
	cos(iTime))/(
	amount/2.0),

x += float(i)/amount;
	}
return 1.0-(n);
	}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/(iResolution.x,
    iResolution.y);

    fragColor = vec4(voronoi(uv*16.0));
}
