#define zoom -5.
#define sensitivity 7.
#define rotaionSpeed .2
//Heavy performance impact, maybe set to 1 if going full screen 
#define AA 2
//Setting this to high can crash the complier
#define maxSteps 60
#define shadows
#define normals
#define AO
#define shadowSoftness 5.
//#define hmmm

float sdSphere(vec3 p, float r){
    return length(p) - r;
}
//https://iquilezles.org/articles/distfunctions
float sminus( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float smin( float a, float b, float k )
{
    float h = a-b;
    return 0.5*( (a+b) - sqrt(h*h+k) );
}
//----

//rotation matrices
mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
mat3 rotz(float a) { mat3 rot; rot[0] = vec3(cos(a), -sin(a), 0.0); rot[1] = vec3(sin(a), cos(a), 0.0); rot[2] = vec3(0.0, 0.0, 1.0); return rot; }



float sdCylenderCross(vec3 p, float r){
    return min(min(
                   length(p.xz),
                   length(p.xy)),
                   length(p.yz)) - r;
}
// distance to the scene and the final position of p
vec4 map(vec3 p) {

    float d = sdSphere(p, 2.);
    if (d > 0.1){return vec4(d,p);}
    float scale = 1.;
    for (int i = 0; i<4;i++){

        d = sminus(
                    sdCylenderCross((p=abs(p)-scale)/scale, 0.16)*scale,
                    d,
                    scale*0.5);
        scale /= 2.;
    }
    return vec4(d, p);
}

//https://iquilezles.org/articles/rmshadows
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    #ifdef shadows
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
    #else
    return .9;
    #endif
}
#define ZERO min(iFrame, 0)
//https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 p )
{
    #ifdef normals
    const float h = 0.0001;
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+e*h).x;
    }
    return normalize(n);
    #else
    return vec3(0);
    #endif
}

//
float calcAO(vec3 pos, vec3 nor)
{
    #ifdef AO
	float occ = 0.0;
    float sca = .4;
    for( int i=ZERO; i<5; i++ )
    {
            float h = 0.01 + 0.25*float(i)/4.0;
        float d = map( pos+h*nor).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
    #else
    return 1.;
    #endif
}

vec3 intersection(vec3 ro, vec3 rd){
    float dist;
    for (int i = 0; i < maxSteps; i++){
        dist = map(ro).x;
        ro += dist*rd;
        if (dist < 0.01)
            break;
    }

    return ro;

}

vec4 render(vec3 ro, vec3 rd){
      vec3 p = intersection(ro,rd);
 vec4 d_pxyz = map(p);
    vec3 trp = d_pxyz.yzw;
  float trap = distance(p, trp);

    //Shading
    vec3 sun = vec3(0., 1., .0);
    float sha = softshadow(p, sun, 0.1, 5., shadowSoftness)*0.5+0.5;
     vec3 nor = calcNormal(p);
    float occ = calcAO(p, nor);
     vec3 tex = texture(iChannel0, reflect(rd, nor)).xyz;
    //float lig = (dot(sun,nor)*0.5+0.5);

    //Colors
    vec3 col = vec3(0);
    vec3 bcl = vec3(sin(tan(trap*2.)), sin(trap), sin(tan(trap)));
    vec3 scl = vec3(0.9, 0.4 ,.3);
    vec3 amb = vec3(0.7, 0.8, 1.);
    //

    //Hit
    col += 0.75*scl*bcl+(tex*sha*sha*sha*sha*sha);
    col += 0.6*amb*occ;
    //No hit
    vec3 bgc = texture(iChannel0, rd).xyz;
    //
    #ifndef hmmm
    col = mix(bgc, col,  min(1.,0.01/d_pxyz.x));
    #else
    col = mix(bgc, col,  min(1.,0.001/d_pxyz.x));
    #endif
    return vec4(col, 1);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3   ro = vec3(0, 0, zoom);
    mat3 xrot;
    mat3 yrot;
    if (iMouse.z > 0.){
        yrot = roty(iMouse.x/iResolution.x*sensitivity);
        xrot = rotx(-iMouse.y/iResolution.y*sensitivity);
    } else {
        yrot = roty(iTime*rotaionSpeed);
        xrot = rotx(0.7);
    }
   ro*=xrot*yrot;


    vec4 tot = vec4(0);

    //Super sampling
    for(int m=0;m<AA;m++){
    for(int n=0;n<AA;n++){
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 u = ((fragCoord+o) / iResolution.xy - 0.5) / vec2(iResolution.y / iResolution.x, 1);
        vec3 rd = normalize(vec3(u, 1));
        tot += render(ro, rd*xrot*yrot) / float(AA*AA);
    }}

    //tone mapping
    tot = smoothstep(0.,1.6,tot);

    // Output to screen
    fragColor = tot;
}
