{  


    frag_coord -= iResolution.xy/200.;
    //frag_color -= frag_color;
	frag_color = vec4(0,0,0,1.0); // keeps alpha

    for (float i=2.; i<40.; i+=2.) {
        uvec2 p = uvec2( frag_coord * (i*.04+.96)+iTime*100. + 10000.); 
        uint temp = ( p.x + uint(100000.*i) ) 
        	          ^  // try ^ & |
        	          p.y;

        frag_color += step(.98, hash11(temp)) * vec4(2.23/3.,1.16/3.,0.12/3.,1.) / i;
    }


}
