#define keyToggle(a)  ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)

void mainImage( out vec4 O, vec2 U )
{
    if ( iFrame < 1 ) { O = vec4(U/R,1,1); return; }
    O = fract(sin(float(iFrame/1) *vec4(127.1,311.7, 269.5,183.3)) *43758.5453123);
    vec2 p = O.xy,                              // choose a random line
         d = cos( 6.28*O.z + vec2(0,1.57) );
    U /= R;
    p -=U; p -= dot(p,d)*d;                     // vector distance to line
    float l = length(p);
    U -= p/l / R;                               // move UVs asside the line
    U = .5/R + U*(1.-1./R);
    O = texture(iChannel0,U);
    if (keyToggle(32))
        O.a *= smoothstep( 0., 1./R.y, l );     // trace fracture
}
