#define PI 3.14
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy/iResolution.xy;
    float cosine = cos(pos.x*2.0*PI + iTime);
    float sine2 = sin(pos.y*2.0*PI*2.0 + iTime);
    float cosine3 = cos(pos.x*2.0*PI*3.0 + iTime);
    vec3 color = vec3(cosine, sine2,  cosine3);
    fragColor = vec4(color, 1.0);
}
