bool Circle(vec2 point, vec2 center, float r){
        float scaleY = iResolution.y / iResolution.x;
        return (point.x - center.x) * (point.x - center.x) + (point.y   - center.y) * (point.y - center.y) * (scaleY * scaleY)  < r*r; 
}

