void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 whiteColor = vec3(smoothstep(((iTime - 4.0) / 10.0), (iTime / 10.0), uv.x));

    vec3 multiColor = vec3(0.0, uv.x, uv.y);

    vec3 col = whiteColor + multiColor;

    fragColor = vec4(col,1.0);
}
