#define C(i, j) texture(iChannel0, uv + vec2(i, j) / iResolution.xy).x
float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = vec4(0.0);
    if(iMouse.w > 0.0 || iFrame == 0) {
        fragColor = rand(vec2(uv.x+iTime, uv.y)) > 0.8 && abs(length(uv-vec2(0.5, 0.5))) < 0.1 ? fragColor = vec4(1.0) : vec4(0.0);
    } else {
        if(texture(iChannel0, uv).x > 0.5) { //alive
            int neighbors = int(C(-1.0, -1.0) + C(0.0, -1.0) + C(1.0, -1.0) + C(-1.0, 0.0) + C(1.0, 0.0) + C(-1.0, 1.0) + C(0.0, 1.0) + C(1.0, 1.0));
            fragColor = (neighbors == 0 || neighbors == 2 || neighbors == 3) ? vec4(1.0) : vec4(0.0);
        } else {
            fragColor = int(C(-1.0, -1.0) + C(0.0, -1.0) + C(1.0, -1.0) + C(-1.0, 0.0) + C(1.0, 0.0) + C(-1.0, 1.0) + C(0.0, 1.0) + C(1.0, 1.0)) == 3 ? vec4(1.0) : vec4(0.0);
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = mix(texture(iChannel0, uv), texture(iChannel1, uv), 0.8);
