// Progressive variant of https://shadertoy.com/view/fscSWH
// Eulerian variant of https://www.shadertoy.com/view/fdcXWH

#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define T(c)     texelFetch(c, ivec2(u) % textureSize(c,0).xy, 0).x 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U, P = u/R;
    u.x += float(iFrame);
    U = u / R;

    float h =    P.y > .66 ? hash(U)
               : P.y > .33 ? T(iChannel0) 
               :             T(iChannel1);
    O = vec4 ( iMouse.z > 0. ? step(P.x,h)               // raw thresholding
                             : R.x/64.* ( h - P.x) +.5   // progressive appear
             );             // 10.
    if ( int(u.y)*3 % int(R.y) == 0 ) O = vec4(1,0,0,1); // horizontal separator
}
