void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/min(iResolution.x,iResolution.y))
              -vec2(fract(max(iResolution.x,iResolution.y)/min(iResolution.x,iResolution.y))/2.0,0.0);

    vec2 center = vec2(0.5, 0.5);
    vec2 delta = uv - center;
    float strength = mix(0., 5., (sin(iTime) + 1.) / 2.);
    float angle = strength * (1.-length(delta));


    float x = cos(angle) * delta.x - sin(angle) * delta.y;
    float y = sin(angle) * delta.x + cos(angle) * delta.y;

    // if movement is wanted
    vec2 offset = vec2(0., 0.) * iTime;
    uv = vec2(x + center.x + offset.x, y + center.y + offset.y);

    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
