float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}


vec3 mix3(vec3 c1, vec3 c2, vec3 c3, float middle_point, float x)
{

    if(x<middle_point)
    {
        float new_x = map(x, 0., middle_point, 0., 1.);
        return mix(c1, c2, new_x);
     } else {
     float new_x = map(x, middle_point, 1.0, 0., 1.);
        return mix(c2, c3, new_x);
     }


}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 c1 = vec3(1.0, 0.0, 0.0);
    vec3 c2 = vec3(0.0, 1.0, 0.0);
    vec3 c3 = vec3(1.0, 0.0, 0.0);


    vec3 col = mix3(c1, c2, c3, 0.5, uv.x);

    fragColor = vec4(col,1.0);
}
