#define RED vec3(1.,0.,0.)
#define GREEN vec3(0.,1.,0.)
#define BLUE vec3(0.,0.,1.)

vec2 ball(vec2 uv, float edge, vec2 pos)
{
    edge *= 10.0;
    uv += pos*.83;
    uv *= 6.0;
    vec3 norm = normalize(vec3(sin(uv.x),sin(uv.y),1.0));
    vec3 ldir = normalize(vec3(iMouse.xy/iResolution.xy-.5-uv,1.7));
    return vec2(clamp(.0,1.0,dot(norm,ldir)*1.1),smoothstep(1.0,1.0-edge,length(uv)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float edge = 3./iResolution.y;
    float lalalalalalalalalalalalalalalalalaalaaaaaa = mod(uv.y*15.0,1.0);

    float inbluuuuuuuuuuuuuue = smoothstep(.666,.666+edge,lalalalalalalalalalalalalalalalalaalaaaaaa);
    float ingreeeeeeeeeeeen = (1.0-inbluuuuuuuuuuuuuue)* smoothstep(.333,.333+edge,lalalalalalalalalalalalalalalalalaalaaaaaa);
    vec3 line = mix(RED,GREEN,ingreeeeeeeeeeeen);
    line = mix(line,BLUE,inbluuuuuuuuuuuuuue);

    fragColor = vec4(line,1.0);

    if(iMouse.z > .5)
    {
        fragColor = vec4(0.);
    }

    for(int i = 0; i < 4; i++)
    {
        vec2 sphajvcacaihbajkchqhjawvam = ball(uv,edge,vec2(cos(iTime*1.1+float(i*27)),sin(iTime*.8489+float(i*27))));
        if (ingreeeeeeeeeeeen != 1.0 && sphajvcacaihbajkchqhjawvam.y != 0.)
        {
            fragColor = vec4(0.921, 0.866, 0.839,1.0)*(1.-ingreeeeeeeeeeeen)*sphajvcacaihbajkchqhjawvam.x*sphajvcacaihbajkchqhjawvam.y+(1.-sphajvcacaihbajkchqhjawvam.y)*fragColor;
        }
        sphajvcacaihbajkchqhjawvam = ball(uv,edge,vec2(cos(iTime*.9322+float(i*53)),sin(iTime*.9489+float(i*53))));
        if (inbluuuuuuuuuuuuuue != 1.0 && sphajvcacaihbajkchqhjawvam.y != 0.)
        {
            fragColor = vec4(0.921, 0.866, 0.839,1.0)*(1.0-inbluuuuuuuuuuuuuue)*sphajvcacaihbajkchqhjawvam.x*sphajvcacaihbajkchqhjawvam.y+(1.-sphajvcacaihbajkchqhjawvam.y)*fragColor;
        }
        sphajvcacaihbajkchqhjawvam = ball(uv,edge,vec2(cos(iTime*1.10794+float(i*84)),sin(iTime*.902349+float(i*84))));
        if ((1.0-inbluuuuuuuuuuuuuue)*(1.0-ingreeeeeeeeeeeen) != 1.0 && sphajvcacaihbajkchqhjawvam.y != 0.)
        {
            fragColor = vec4(0.921, 0.866, 0.839,1.0)*sphajvcacaihbajkchqhjawvam.x*sphajvcacaihbajkchqhjawvam.y+(1.-sphajvcacaihbajkchqhjawvam.y)*fragColor;
        }
    }

}
