    /3. - i.x,
    ty = -0.5+cos(iTime*3.)
    /3. + i.y
    , s = cir(0.1,0.1,

    /**Square**/
    abs((tx-o.x)+(ty+o.y))+abs((tx-o.x)-(ty+o.y))

    /**Sphere**/
//    0.5-vec2(-.4+o.x-sin(iTime*2.)*.3,o.y-cos(iTime*2.)*.2)
    )
    ,
    c = i.x > 0.90
    ? abs(i.y)+1.05*s : 
    i.y/s
    //s=step(s,o.y)
    ;

o= vec4(c - i.y);
}
